def test_list_agents_pager(transport_name: str = "grpc"):
    client = AgentServiceClient(
        credentials=ga_credentials.AnonymousCredentials(),
        transport=transport_name,
    )

    # Mock the actual call within the gRPC stub, and fake the request.
    with mock.patch.object(type(client.transport.list_agents), "__call__") as call:
        # Set the response to a series of pages.
        call.side_effect = (
            agent_service.ListAgentsResponse(
                agents=[
                    agent.Agent(),
                    agent.Agent(),
                    agent.Agent(),
                ],
                next_page_token="abc",
            ),
            agent_service.ListAgentsResponse(
                agents=[],
                next_page_token="def",
            ),
            agent_service.ListAgentsResponse(
                agents=[
                    agent.Agent(),
                ],
                next_page_token="ghi",
            ),
            agent_service.ListAgentsResponse(
                agents=[
                    agent.Agent(),
                    agent.Agent(),
                ],
            ),
            RuntimeError,
        )

        expected_metadata = ()
        retry = retries.Retry()
        timeout = 5
        expected_metadata = tuple(expected_metadata) + (
            gapic_v1.routing_header.to_grpc_metadata((("parent", ""),)),
        )
        pager = client.list_agents(request={}, retry=retry, timeout=timeout)

        assert pager._metadata == expected_metadata
        assert pager._retry == retry
        assert pager._timeout == timeout

        results = list(pager)
        assert len(results) == 6
        assert all(isinstance(i, agent.Agent) for i in results)


