def test_list_tools_pager(transport_name: str = "grpc"):
    client = AgentServiceClient(
        credentials=ga_credentials.AnonymousCredentials(),
        transport=transport_name,
    )

    # Mock the actual call within the gRPC stub, and fake the request.
    with mock.patch.object(type(client.transport.list_tools), "__call__") as call:
        # Set the response to a series of pages.
        call.side_effect = (
            agent_service.ListToolsResponse(
                tools=[
                    tool.Tool(),
                    tool.Tool(),
                    tool.Tool(),
                ],
                next_page_token="abc",
            ),
            agent_service.ListToolsResponse(
                tools=[],
                next_page_token="def",
            ),
            agent_service.ListToolsResponse(
                tools=[
                    tool.Tool(),
                ],
                next_page_token="ghi",
            ),
            agent_service.ListToolsResponse(
                tools=[
                    tool.Tool(),
                    tool.Tool(),
                ],
            ),
            RuntimeError,
        )

        expected_metadata = ()
        retry = retries.Retry()
        timeout = 5
        expected_metadata = tuple(expected_metadata) + (
            gapic_v1.routing_header.to_grpc_metadata((("parent", ""),)),
        )
        pager = client.list_tools(request={}, retry=retry, timeout=timeout)

        assert pager._metadata == expected_metadata
        assert pager._retry == retry
        assert pager._timeout == timeout

        results = list(pager)
        assert len(results) == 6
        assert all(isinstance(i, tool.Tool) for i in results)


