def test_create_app_version_rest_interceptors(null_interceptor):
    transport = transports.AgentServiceRestTransport(
        credentials=ga_credentials.AnonymousCredentials(),
        interceptor=None
        if null_interceptor
        else transports.AgentServiceRestInterceptor(),
    )
    client = AgentServiceClient(transport=transport)

    with mock.patch.object(
        type(client.transport._session), "request"
    ) as req, mock.patch.object(
        path_template, "transcode"
    ) as transcode, mock.patch.object(
        transports.AgentServiceRestInterceptor, "post_create_app_version"
    ) as post, mock.patch.object(
        transports.AgentServiceRestInterceptor, "post_create_app_version_with_metadata"
    ) as post_with_metadata, mock.patch.object(
        transports.AgentServiceRestInterceptor, "pre_create_app_version"
    ) as pre:
        pre.assert_not_called()
        post.assert_not_called()
        post_with_metadata.assert_not_called()
        pb_message = agent_service.CreateAppVersionRequest.pb(
            agent_service.CreateAppVersionRequest()
        )
        transcode.return_value = {
            "method": "post",
            "uri": "my_uri",
            "body": pb_message,
            "query_params": pb_message,
        }

        req.return_value = mock.Mock()
        req.return_value.status_code = 200
        req.return_value.headers = {"header-1": "value-1", "header-2": "value-2"}
        return_value = gcc_app_version.AppVersion.to_json(gcc_app_version.AppVersion())
        req.return_value.content = return_value

        request = agent_service.CreateAppVersionRequest()
        metadata = [
            ("key", "val"),
            ("cephalopod", "squid"),
        ]
        pre.return_value = request, metadata
        post.return_value = gcc_app_version.AppVersion()
        post_with_metadata.return_value = gcc_app_version.AppVersion(), metadata

        client.create_app_version(
            request,
            metadata=[
                ("key", "val"),
                ("cephalopod", "squid"),
            ],
        )

        pre.assert_called_once()
        post.assert_called_once()
        post_with_metadata.assert_called_once()


