def test_agent_service_base_transport():
    # Instantiate the base transport.
    with mock.patch(
        "google.cloud.ces_v1beta.services.agent_service.transports.AgentServiceTransport.__init__"
    ) as Transport:
        Transport.return_value = None
        transport = transports.AgentServiceTransport(
            credentials=ga_credentials.AnonymousCredentials(),
        )

    # Every method on the transport should just blindly
    # raise NotImplementedError.
    methods = (
        "list_apps",
        "get_app",
        "create_app",
        "update_app",
        "delete_app",
        "export_app",
        "import_app",
        "list_agents",
        "get_agent",
        "create_agent",
        "update_agent",
        "delete_agent",
        "list_examples",
        "get_example",
        "create_example",
        "update_example",
        "delete_example",
        "list_tools",
        "get_tool",
        "list_conversations",
        "get_conversation",
        "delete_conversation",
        "batch_delete_conversations",
        "create_tool",
        "update_tool",
        "delete_tool",
        "list_guardrails",
        "get_guardrail",
        "create_guardrail",
        "update_guardrail",
        "delete_guardrail",
        "list_deployments",
        "get_deployment",
        "create_deployment",
        "update_deployment",
        "delete_deployment",
        "list_toolsets",
        "get_toolset",
        "create_toolset",
        "update_toolset",
        "delete_toolset",
        "list_app_versions",
        "get_app_version",
        "create_app_version",
        "delete_app_version",
        "restore_app_version",
        "list_changelogs",
        "get_changelog",
        "get_location",
        "list_locations",
        "get_operation",
        "cancel_operation",
        "delete_operation",
        "list_operations",
    )
    for method in methods:
        with pytest.raises(NotImplementedError):
            getattr(transport, method)(request=object())

    with pytest.raises(NotImplementedError):
        transport.close()

    # Additionally, the LRO client (a property) should
    # also raise NotImplementedError
    with pytest.raises(NotImplementedError):
        transport.operations_client

    # Catch all for all remaining methods and properties
    remainder = [
        "kind",
    ]
    for r in remainder:
        with pytest.raises(NotImplementedError):
            getattr(transport, r)()


