def test_evaluation_service_base_transport():
    # Instantiate the base transport.
    with mock.patch(
        "google.cloud.ces_v1beta.services.evaluation_service.transports.EvaluationServiceTransport.__init__"
    ) as Transport:
        Transport.return_value = None
        transport = transports.EvaluationServiceTransport(
            credentials=ga_credentials.AnonymousCredentials(),
        )

    # Every method on the transport should just blindly
    # raise NotImplementedError.
    methods = (
        "run_evaluation",
        "upload_evaluation_audio",
        "create_evaluation",
        "generate_evaluation",
        "import_evaluations",
        "create_evaluation_dataset",
        "update_evaluation",
        "update_evaluation_dataset",
        "delete_evaluation",
        "delete_evaluation_result",
        "delete_evaluation_dataset",
        "delete_evaluation_run",
        "get_evaluation",
        "get_evaluation_result",
        "get_evaluation_dataset",
        "get_evaluation_run",
        "list_evaluations",
        "list_evaluation_results",
        "list_evaluation_datasets",
        "list_evaluation_runs",
        "list_evaluation_expectations",
        "get_evaluation_expectation",
        "create_evaluation_expectation",
        "update_evaluation_expectation",
        "delete_evaluation_expectation",
        "create_scheduled_evaluation_run",
        "get_scheduled_evaluation_run",
        "list_scheduled_evaluation_runs",
        "update_scheduled_evaluation_run",
        "delete_scheduled_evaluation_run",
        "test_persona_voice",
        "get_location",
        "list_locations",
        "get_operation",
        "cancel_operation",
        "delete_operation",
        "list_operations",
    )
    for method in methods:
        with pytest.raises(NotImplementedError):
            getattr(transport, method)(request=object())

    with pytest.raises(NotImplementedError):
        transport.close()

    # Additionally, the LRO client (a property) should
    # also raise NotImplementedError
    with pytest.raises(NotImplementedError):
        transport.operations_client

    # Catch all for all remaining methods and properties
    remainder = [
        "kind",
    ]
    for r in remainder:
        with pytest.raises(NotImplementedError):
            getattr(transport, r)()


