def test_evaluation_service_client_transport_session_collision(transport_name):
    creds1 = ga_credentials.AnonymousCredentials()
    creds2 = ga_credentials.AnonymousCredentials()
    client1 = EvaluationServiceClient(
        credentials=creds1,
        transport=transport_name,
    )
    client2 = EvaluationServiceClient(
        credentials=creds2,
        transport=transport_name,
    )
    session1 = client1.transport.run_evaluation._session
    session2 = client2.transport.run_evaluation._session
    assert session1 != session2
    session1 = client1.transport.upload_evaluation_audio._session
    session2 = client2.transport.upload_evaluation_audio._session
    assert session1 != session2
    session1 = client1.transport.create_evaluation._session
    session2 = client2.transport.create_evaluation._session
    assert session1 != session2
    session1 = client1.transport.generate_evaluation._session
    session2 = client2.transport.generate_evaluation._session
    assert session1 != session2
    session1 = client1.transport.import_evaluations._session
    session2 = client2.transport.import_evaluations._session
    assert session1 != session2
    session1 = client1.transport.create_evaluation_dataset._session
    session2 = client2.transport.create_evaluation_dataset._session
    assert session1 != session2
    session1 = client1.transport.update_evaluation._session
    session2 = client2.transport.update_evaluation._session
    assert session1 != session2
    session1 = client1.transport.update_evaluation_dataset._session
    session2 = client2.transport.update_evaluation_dataset._session
    assert session1 != session2
    session1 = client1.transport.delete_evaluation._session
